INSERT INTO Books (title, annotation, pathToFile) VALUES
('Идиот', 'Роман Ф. М. Достоевского «Идиот» – о «положительно прекрасном человеке», безгрешном, почти идеальном, который пытается любить «ближнего, как самого себя»... ', '/img/Books/1.webp'),
('Преступление и наказание', 'При этом криминальная история стала для автора не только темой, но и поводом для размышлений о социальных обстоятельствах, толкающих человека на преступления, а также возможностью показать, какие сложные «химические» процессы происходят в душах людей.', '/img/Books/2.webp'),
('Бесы', 'Роман-памфлет о зарождающемся терроризме и анархизме раскрывает трагедию русской души, в которой поселились бесы.', '/img/Books/3.webp'),
('Братья Карамазовы', 'В романе рассматриваются темы отцеубийства, веры, морали и существования Бога.', '/img/Books/4.webp'),
('Сияние', '…Проходят годы, десятилетия, но потрясающая история писателя Джека Торранса, его сынишки Дэнни, наделенного необычным даром, и поединка с темными силами, обитающими в роскошном отеле... ', '/img/Books/5.webp'),
('Мизери', 'Роман, название которого в переводе означает «Страдание» приснился автору. Может ли спасение от верной гибели обернуться таким кошмаром, что даже смерть покажется милосердным даром... ', '/img/Books/6.webp'),
('Долгая прогулка', 'Это была страшная игра – игра на выживание. Это была Долгая Прогулка. Прогулка со Смертью, ибо смерть ожидала каждого упавшего. Дорога к счастью – потому что победивший в игре получал все.', '/img/Books/7.webp'),
('Кладбище домашних животных', 'История группы друзей, борющихся с потусторонней тварью, питающейся детскими страхами. Автор приоткрывает свои собственные страхи, создавая атмосферу, которая пугает и заставляет задуматься о смерти.', '/img/Books/8.webp'),
('Зелёная миля', 'Стивен Кинг приглашает слушателей в жуткий мир тюремного блока смертников, откуда уходят, чтобы не вернуться, приоткрывает дверь последнего пристанища тех, кто преступил закон и теперь отсчитывает последние часы…', '/img/Books/9.webp'),
('11-22-63', '…Убийство президента Кеннеди стало самым трагическим событием американской истории ХХ века. Тайна его до сих пор не раскрыта. Но что, если случится чудо? Если появится возможность отправиться в прошлое и предотвратить катастрофу?', '/img/Books/10.webp'),
('Жизнь взаймы', 'Начало 50-х годов. Автогонщик Клэрфе приезжает навестить своего давнего друга в санаторий «Монтана». Там же он знакомится со смертельно больной девушкой Лилиан...', '/img/Books/11.webp'),
('Три товарища', 'Это удивительная книга, которую нельзя пропустить и оставить без внимания хотя бы ради того, чтобы понять, что существует на свете что-то более важное и ценное, чем деньги, карьера и успех, то, что не проходит с годами и способно творить чудеса и изменять людей.', '/img/Books/12.webp'),
('Черный обелиск', 'В романе «Черный обелиск» (1956) Эрих Мария Ремарк блестяще воссоздал атмосферу Германии 20-х годов. Время между двумя мировыми войнами… Время зарождения фашизма… Время, которое писатель обозначил названием еще одного своего романа – «Время жить и время умирать»… ', '/img/Books/13.webp'),
('Триумфальная арка', 'Место действия – Париж накануне Второй мировой войны. Герой – хирург, спасающий человеческие жизни, беженец из Германии, без документов, скрывающийся и от французов, и от нацистов. Героиня – итальянская актриса, окруженная поклонниками, неотразимая и вспыльчивая, как все артисты.', '/img/Books/14.webp'),
('На западном фронте без перемен', 'Этот роман является первой частью трилогии, которую автор посвятил Первой мировой войне и судьбе солдат, прошедших эту войну', '/img/Books/15.webp'),
('Вишневый сад', 'Новаторские для своего времени пьесы А. П. Чехова сделали его имя бессмертным и в литературе, и в истории театра. ', '/img/Books/16.webp'),
('Дама с собачкой', 'Однажды в Ялте Гуров замечает "даму с собачкой" - молодую блондинку с белым шпицем.', '/img/Books/17.webp'),
('Каштанка', 'Сборник включает рассказы великого русского писателя Антона Павловича Чехова о детях и животных. Истории, порой трагичные, а порой смешные, заставят читателя задуматься о дружбе, честности, проблеме выбора и ответственности за слабых.', '/img/Books/18.webp'),
('Война и мир', 'Главная тема – историческая судьба русского народа в Отечественной войне 1812 года. В романе выведено более 550 персонажей, как вымышленных, так и исторических. Лучших своих героев Л.Н. Толстой изображает во всей их душевной сложности, в непрерывных поисках истины, в стремлении к самосовершенствованию.', '/img/Books/19.webp'),
('Анна Каренина', 'История любви замужней женщины к блестящему офицеру, губительной для Анны, но завораживающей своей страстностью и самоотверженностью, уже более ста лет не сходит со сценических подмостков и экранизирована множество раз как в России, так и за рубежом.', '/img/Books/20.webp'),
('Воскресение', 'История князя Нехлюдова, его мучительной переоценки жизненных ценностей поучительна для каждого. Писатели и их великие произведения для того и существуют, чтобы от сочувствия героям перейти к пониманию самих себя.', '/img/Books/21.webp'),
('Детство. Отрочество. Юность', 'знаменитая трилогия Льва Николаевича Толстого «Детство. Отрочество. Юность» – трилогия, положившая начало творческому пути великого русского писателя, сделавшая его знаменитым', '/img/Books/22.webp'),
('Трудно быть богом', 'История землянина, ставшего «наблюдателем» на планете, застрявшей в эпохе позднего средневековья, и принужденного «не вмешиваться» в происходящее, экранизирована уже несколько раз – однако даже лучший фильм не в силах передать всего таланта книги, на основе которой он снят!..', '/img/Books/23.webp'),
('Обитаемый остров', 'Фантастическая повесть «Обитаемый остров» рассказывает о судьбе молодого землянина, человека XXII века, попавшего на неизвестную планету.', '/img/Books/24.webp'),
('Пикник на обочине', '…После посещения Земли пришельцами на планете появились Зоны – загадочные места, полные опасностей и смертельных ловушек. Здесь немало артефактов – непонятных устройств и предметов, обладающих необычными свойствами. ', '/img/Books/25.webp');
